    ODB_POTENTIALLY_UNUSED (db);

    // person base
    //
    if (--d != 0)
      base_traits::init (o, *i.base, db);

    // email_
    //
    {
      ::std::string& v =
        o.email_;

      sqlite::value_traits<
          ::std::string,
          sqlite::id_text >::set_value (
        v,
        i.email_value,
        i.email_size,
        i.email_null);
    }
