  bool access::object_traits_impl< ::employee, id_sqlite >::
  grow (image_type& i,
        bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // id_
    //
    t[0UL] = false;

    // name_
    //
    if (composite_value_traits< ::employee::name_type, id_sqlite >::grow (
          i.name_value, t + 1UL))
      grew = true;

    // employer_
    //
    if (t[3UL])
    {
      i.employer_value.capacity (i.employer_size);
      grew = true;
    }

    return grew;
  }

  void access::object_traits_impl< ::employee, id_sqlite >::
  bind (sqlite::bind* b,
        image_type& i,
        sqlite::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace sqlite;

    std::size_t n (0);

    // id_
    //
    if (sk != statement_update)
    {
      b[n].type = sqlite::bind::integer;
      b[n].buffer = &i.id_value;
      b[n].is_null = &i.id_null;
      n++;
    }

    // name_
    //
    composite_value_traits< ::employee::name_type, id_sqlite >::bind (
      b + n, i.name_value, sk);
    n += 2UL;

    // employer_
    //
    b[n].type = sqlite::image_traits<
      ::std::string,
      sqlite::id_text>::bind_value;
    b[n].buffer = i.employer_value.data ();
    b[n].size = &i.employer_size;
    b[n].capacity = i.employer_value.capacity ();
    b[n].is_null = &i.employer_null;
    n++;
  }

  void access::object_traits_impl< ::employee, id_sqlite >::
  bind (sqlite::bind* b, id_image_type& i)
  {
    std::size_t n (0);
    b[n].type = sqlite::bind::integer;
    b[n].buffer = &i.id_value;
    b[n].is_null = &i.id_null;
  }

  bool access::object_traits_impl< ::employee, id_sqlite >::
  init (image_type& i,
        const object_type& o,
        sqlite::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace sqlite;

    bool grew (false);

    // id_
    //
    if (sk == statement_insert)
    {
      long unsigned int const& v =
        o.id_;

      bool is_null (false);
      sqlite::value_traits<
          long unsigned int,
          sqlite::id_integer >::set_image (
        i.id_value,
        is_null,
        v);
      i.id_null = is_null;
    }

    // name_
    //
    {
      ::employee::name_type const& v =
        o.name_;

      if (composite_value_traits< ::employee::name_type, id_sqlite >::init (
            i.name_value,
            v,
            sk))
        grew = true;
    }

    // employer_
    //
    {
      ::std::tr1::shared_ptr< ::employer > const& v =
        o.employer_;

      typedef object_traits< ::employer > obj_traits;
      typedef odb::pointer_traits< ::std::tr1::shared_ptr< ::employer > > ptr_traits;

      bool is_null (ptr_traits::null_ptr (v));
      if (!is_null)
      {
        const obj_traits::id_type& id (
          obj_traits::id (ptr_traits::get_ref (v)));

        std::size_t cap (i.employer_value.capacity ());
        sqlite::value_traits<
            obj_traits::id_type,
            sqlite::id_text >::set_image (
          i.employer_value,
          i.employer_size,
          is_null,
          id);
        i.employer_null = is_null;
        grew = grew || (cap != i.employer_value.capacity ());
      }
      else
        throw null_pointer ();
    }

    return grew;
  }

  void access::object_traits_impl< ::employee, id_sqlite >::
  init (object_type& o,
        const image_type& i,
        database* db)
  {
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (db);

    // id_
    //
    {
      long unsigned int& v =
        o.id_;

      sqlite::value_traits<
          long unsigned int,
          sqlite::id_integer >::set_value (
        v,
        i.id_value,
        i.id_null);
    }

    // name_
    //
    {
      ::employee::name_type& v =
        o.name_;

      composite_value_traits< ::employee::name_type, id_sqlite >::init (
        v,
        i.name_value,
        db);
    }

    // employer_
    //
    {
      ::std::tr1::shared_ptr< ::employer >& v =
        o.employer_;

      typedef object_traits< ::employer > obj_traits;
      typedef odb::pointer_traits< ::std::tr1::shared_ptr< ::employer > > ptr_traits;

      if (i.employer_null)
        v = ptr_traits::pointer_type ();
      else
      {
        obj_traits::id_type id;
        sqlite::value_traits<
            obj_traits::id_type,
            sqlite::id_text >::set_value (
          id,
          i.employer_value,
          i.employer_size,
          i.employer_null);

        // If a compiler error points to the line below, then
        // it most likely means that a pointer used in a member
        // cannot be initialized from an object pointer.
        //
        v = ptr_traits::pointer_type (
          static_cast<sqlite::database*> (db)->load<
            obj_traits::object_type > (id));
      }
    }
  }

  void access::object_traits_impl< ::employee, id_sqlite >::
  init (id_image_type& i, const id_type& id)
  {
    {
      bool is_null (false);
      sqlite::value_traits<
          long unsigned int,
          sqlite::id_integer >::set_image (
        i.id_value,
        is_null,
        id);
      i.id_null = is_null;
    }
  }

